import React from "react";
import './NewExpense.css';
import ExpenseForm from "./ExpenseForm";

const NewExpense = (props)=>{


  const [showForm, setShowFormValue] = useState(false);

    const saveExpenseDataHandler =(data)=>{
        const expenseData ={
            ...data,
            id:Math.random().toString()
        }
        props.onAddExpense(expenseData);
    }

    const addNewClickHandler = () => {
        setShowFormValue(true);
      };

    return(
    <div className="new-expense">

      <button onClick={addNewClickHandler} className="new-expense__actions">Add New Expense </button>
        <ExpenseForm onSaveExpense={saveExpenseDataHandler} />

    </div>
    )
}

export default NewExpense;
