import { useState } from "react";
import "./ExpenseForm.css";

const ExpenseForm = (props) => {
  const [enteredTitle, setEnteredTitle] = useState("");
  const [enteredAmount, setEnterAmount] = useState("");
  const [enteredDate, setEnteredDate] = useState("");
  const [showForm, setShowFormValue] = useState(false);

  const titleChangeHandler = (event) => {
    setEnteredTitle(event.target.value);
  };

  const amountChangeHandler = (event) => {
    setEnterAmount(event.target.value);
  };

  const dateChangeHandler = (event) => {
    setEnteredDate(event.target.value);
  };

  const submitHandler = (event) => {
    event.preventDefault();

    const expenseData = {
      title: enteredTitle,
      amount: enteredAmount,
      date: new Date(enteredDate),
    };
    props.onSaveExpense(expenseData);
    setEnterAmount("");
    setEnteredDate("");
    setEnteredTitle("");
    cancelHandler();
  };

  const cancelHandler = () => {
    setShowFormValue(false);
  };

  const addNewClickHandler = () => {
    setShowFormValue(true);
  };

  const fdata = showForm?<form onSubmit={submitHandler}>
  <div className="new-expense__controls">
    <div className="new-expense__control">
      <label>Title</label>
      <input
        value={enteredTitle}
        onChange={titleChangeHandler}
        type="text"
      />
    </div>
  </div>
  <div className="new-expense__controls">
    <div className="new-expense__control">
      <label>Amount</label>
      <input
        value={enteredAmount}
        onChange={amountChangeHandler}
        type="numer"
        min="0.01"
        step="0.01"
      />
    </div>
  </div>
  <div className="new-expense__controls">
    <div className="new-expense__control">
      <label>Date</label>
      <input
        value={enteredDate}
        onChange={dateChangeHandler}
        type="Date"
        min="2019-01-01"
        max="2022-12-31"
      />
    </div>
  </div>
  <div className="new-expense__actions">
    <button onClick={cancelHandler}>Cancel</button>
    <button type="submit">Add Expense</button>
  </div>
</form>: <button onClick={addNewClickHandler} className="new-expense__actions">
        Add New Expense
      </button>

  return (
    <>



    </>
  );
};

export default ExpenseForm;
